import { PureComponent } from 'react';

import './CheckoutSteps.style.scss'

import {
    BILLING_STEP, DETAILS_STEP, SHIPPING_STEP,
} from 'SourceRoute/Checkout/Checkout.config';


export class CheckoutSteps extends PureComponent {

    stepMap = {
        [SHIPPING_STEP]: {
            title: __('Shipping step'),
        },
        [BILLING_STEP]: {
            title: __('Billing step'),
        },
        [DETAILS_STEP]: {
            title: __('Success!'),
        }
    }

    allSteps() {
        return [SHIPPING_STEP, BILLING_STEP, DETAILS_STEP]
    }

    renderTitle() {
        const { checkoutStep } = this.props
        const { title } = this.stepMap[checkoutStep]

        return (
            title
        )
    }

    render() {
        return (
            <div className="CheckoutSteps">
                {this.allSteps().map((step, id) => {
